class Solution {
public:
    vector<int> addToArrayForm(vector<int>& num, int k) {
        int nums = 0;
        int tens = 1;
        vector<int> result;
        for(int i = num.size() - 1; i >= 0; i --){
            nums += num[i] * tens;
            tens *= 10;
        }
        nums += k;
