class Solution {
public:
    vector<int> shortestToChar(string s, char c) {
        int size1 = s.size();
        vector<int> result(size1, 1);
        for(int i = 0; i < size1 ; i ++){
            if(s[i] == c){
                result[i] = 0;
            }
        }

        for(int k = 0; k < size1; k ++){
            int i = k;
            int j = k;
            if(result[k] != 0){
                while(result[i] != 0 && result[j] != 0){
                    i --; if(i < 0) i = 0;
                    j ++; if(j > size1 - 1) j = size1 - 1;
                }
                if(result[i] == 0) {
                    result[k] = k - i;
                } else{
                    result[k] = j - k;
                }
            } 
        }
        return result;
    }
};
