    public int[] shortestToChar(String s, char c) {
        List<Integer> c_index= new ArrayList<Integer>();
        int result[] = new int[s.length()];

        for(int i=0; i<s.length(); i++){
            if(c ==s.charAt(i)){
                c_index.add(i);
            }
        }
        int first= 0;
        int second = 0;

        for(int i=0; i<s.length(); i++){
            if(c_index.size()>1 && second<c_index.size() && i>=c_index.get(second)){
                 first = second;
                 if(second != c_index.size()-1 ){
                     second++;
                 }
            }

            if(i <= c_index.get(first)){
                result[i] =Math.abs(c_index.get(first)-i);
            }else if(i>c_index.get(first) && i<c_index.get(second)){
                result[i] =Math.min(Math.abs(c_index.get(first)-i),Math.abs(c_index.get(second)-i));
            }else{
                result[i] =Math.abs(c_index.get(second)-i);
            }
        }

        return result;
    }
