    const res = Array(s.length).fill(Infinity)
    for (let i = 0; i < s.length; i++) {
        if (s[i] !== c && cIndex < 0) {
            continue
        }
        if (s[i] === c) {
            cIndex = i
            res[i] = 0
        } else {
            res[i] = Math.min(i - cIndex, res[i])
        }
    }
    cIndex = -1;
    for (let i = s.length - 1; i >= 0; i--) {
        if (s[i] !== c && cIndex < 0) {
            continue
        }
        if (s[i] === c) {
            cIndex = i
        } else {
            res[i] = Math.min(cIndex - i, res[i])
        }
    }
    return res
};

