class Solution {
public:
    int ComputerDis(string s,int current,char c){
        //åœ¨sä¸­è®¡ç®—cç¦»currentæœ€è¿‘çš„å­—ç¬¦.
        if(s[current] == c)
            return 0;
        int left = current - 1;int right = current + 1;
        while(left >=0 && right <= s.size() - 1){
            if(s[left] == c || s[right] == c){
                return right - current;
            }
            else{
                left--;right++;
            }
        }
        while(left >= 0){
            if(s[left] == c){
                return current - left;
            }
            else
                left--;
        }
        while(right <= s.size() - 1){
            if(s[right] == c){
                return right - current;
            }else
                right++;
        }
        return -1;
    }
    vector<int> shortestToChar(string s, char c){
        vector<int> ans;
        for(int i = 0;i<s.size();i++){
            ans.push_back(ComputerDis(s,i,c));
        }
        return ans;
    }
};
