public class Solution {
    public int[] ShortestToChar(string s, char c) {
        var charArr = s.ToCharArray();
            var intArr = new int[charArr.Length];

            int lastIndex = -1, aheadIndex = s.IndexOf(c);
            for (var i = 0; i < charArr.Length; i++)
