class Solution {
    public int[] shortestToChar(String s, char c) {
        int[] answer = new int[s.length()];
        int min1 = Integer.MAX_VALUE;
        int min2 = Integer.MAX_VALUE;
        for(int i = 0 ; i < s.length() ; i++){
            if(s.charAt(i) == c){
                answer[i] = 0;
                continue;
