            for(int j = i ; j < s.length() ; j++){
                if(s.charAt(j) == c){
                    min1 = Math.abs(i-j);
                    break;
                }
            }
            for(int j = i ; j > -1 ; j--){
                if(s.charAt(j) == c){
                    min2 = Math.abs(i-j);
                    break;
                }
            }
            answer[i] = Math.min(min1,min2);
            min1 = Integer.MAX_VALUE;
            min2 = Integer.MAX_VALUE;
        }
        return answer;
    }
}
