class Solution {
    public int[] shortestToChar(String S, char C) {
        int length = S.length();
        int[] res = new int[length];
        int prev = S.indexOf(C),next = S.indexOf(C, prev+1);
        for (int i = 0; i < length; i++){
            res[i] = Math.abs(i-prev);
            if(next!=-1){
                res[i] = Math.min(res[i],next-i);
                if(i==next){
                    prev = next;
                    next = S.indexOf(C,next+1);
                }
            }
        }
        return res;

    }
}
