public int[] shortestToChar(String s, char c) {
        int[] res = new int[s.length()];
        int index = -1;
        for (int i=0; i<res.length; i++) {
            if (s.charAt(i) == c) {
                res[i] = 0;
                index = i;
            }
            else if (index == -1) res[i] = Integer.MAX_VALUE;
            else res[i] = i - index;
        }
        index = -1;
        for (int i=res.length-1; i>=0; i--) {
            if (s.charAt(i) == c) {
                index = i;
            }
            else if (index == -1) res[i] = Math.min(Integer.MAX_VALUE, res[i]);
            else res[i] = Math.min(index - i, res[i]);
        }
        return res;
    }
