class Solution {
public:
    vector<int> addToArrayForm(vector<int>& num, int k) {

        vector<int> res;

        int carry_bit = 0;

        int loc = num.size() - 1;

        while(loc>-1 || k > 0 || carry_bit > 0){
            int num1 = loc>-1?num[loc]:0;
            int num2 = k % 10;

            int value = num1 + num2 + carry_bit;

            res.emplace_back((value % 10));
            carry_bit = value / 10; 

            --loc;
            k /= 10;
        }
        
        reverse(res.begin(),res.end());

        return res;
    }
};
