public class Solution {

    public int[] shortestToChar(String s, char c) {
        int len = s.length();
        int[] res = new int[len];

        int pre = Integer.MIN_VALUE / 2;
        for (int i = 0; i < len; i++) {
            if (c == s.charAt(i)) pre = i;
            res[i] = i - pre;
        }

        int post = Integer.MAX_VALUE / 2;
        for (int i = len - 1; i >= 0; i--) {
            if (c == s.charAt(i)) post = i;
            res[i] = Integer.min(res[i],post - i);
        }

        return res;
    }
}
