var shortestToChar = function(s,c){
    let res = Array(s.length).fill(0);
    

    for(let i=0; i<s.length; i++){
        if (s[i] === c) continue;

        let l = i;
        let r = i;
        let shortest = Infinity;

        while(l>=0){
            if(s[l]===c){
                shortest = Math.min(shortest, i-l); 
                break;
            }
            l--;
        }
        while(r<s.length){
            if(s[r]===c){
                shortest = Math.min(shortest,r-i);
                break;
            }
            r++;
        }
        res[i]=shortest;
    }
    return res;
}

```

