class Solution {
    public List<Integer> addToArrayForm(int[] num, int k) {
        List<Integer> res=new LinkedList<Integer>();
        int n=num.length-1;int flag=0;
        while(n>-1||k!=0||flag!=0){
            int tep;
            if(n<0) tep=k%10+flag;
            else tep=k%10+num[n]+flag;
            k/=10;
            flag=tep/10;
            tep=tep%10;
