function addToArrayForm(num: number[], k: number): number[] {
    const len = num.length;    
    let i = len - 1;
    let carry = 0;
    while (i >= 0) {
        let addNum = 0;
        if (k !== 0) {
            addNum = k % 10;
            k = Math.floor(k / 10);
        }
        let sum = num[i] + carry + addNum;
        carry = sum >= 10 ? 1 : 0;
        num[i] = sum % 10;
        i--;
    }
    k += carry;
    while (k !== 0) {
        num.unshift(k % 10);
        k = Math.floor(k / 10);
    }
    return num;
};
