var addToArrayForm = function(num, k) {
    k = k.toString();
    let aLen = num.length, kLen = k.length;
    num.reverse();
    if(aLen > kLen) {
        k = '0'.repeat(aLen - kLen) + k;
    } else if(aLen < kLen) {
        for(let i = 0; i < kLen - aLen; i++) {
            num.push(0);
        }
    }
    k = k.split('').reverse();
    let sum = 0, carry = 0, res = [];
    num.forEach((item, i)=>{
        sum = item + + k[i] + carry;
        carry = parseInt(sum / 10);
        res.push(sum % 10)
    })
    if(carry > 0) {
        res.push(carry)
    }
    return res.reverse();
};
