public class mindistant {
    public int[] addToArrayForm(String S, char C) {
        int N = S.length();
        int[] res = new int[N];
        for(int i = 0; i <N; i++) {
            if (S.charAt(i) == C) continue;
            int left = i;
            int right = i;
            int dist = 0;
            while (left >= 0 || right <= S.length() - 1) {
                if (S.charAt(left) == C) {
                    dist = i - left;
                    break;
                }
                if (S.charAt(right) == C) {
                    dist = right - i;
                    break;
                }
                if (left > 0) left--;
                if (right < S.length() - 1) right++;
            }
            res[i] = dist;
        }
        return res;
    }
}

