function shortestToChar(s: string, c: string): number[] {
    let result: number[] = [];
    if (s.length === 0 || c.length === 0) {
        return result;
    }
    let strArray = Array.from(s);
    let indexs: number[] = [];
    strArray.forEach((element, index) => {
        if (element === c) {
            indexs.push(index);
        }
    });
    for (let i = 0; i <= indexs[0]; i++) {
        result.push(indexs[0] - i);
    }
    for (let i = 0; i <= indexs.length - 2; i++) {
        let start = indexs[i];
        let end = indexs[i + 1];
        for (let i = start + 1; i <= end; i++) {
            let distance = Math.min(i - start, end - i);
            result.push(distance);
        }
    }
    for (let i = indexs[indexs.length - 1] + 1; i < strArray.length; i++) {
        result.push(i - indexs[indexs.length - 1]);
    }
    return result;
};
