```java
class Solution {
    public int[] shortestToChar(String s, char c) {
        int length = s.length();
        int[] answer = new int[length];
        for (int i = 0; i < length; i++) {
            if (s.charAt(i) == c) { answer[i] = 0; continue; }
            int minDistance = Math.min(leftDistance(s, c, i), rightDistance(s, c, i));
            answer[i] = minDistance;
        }
        return answer;
    }
    public int leftDistance(String s, char c, int index) {
        int counter = 0;
        for (int i = index; i >= 0; i--) {
            if (s.charAt(i) == c) { return counter; }
            counter++;
        }
        return s.length() + 1;
    }
    public int rightDistance(String s, char c, int index) {
        int counter = 0;
        for (int i = index; i < s.length(); i++) {
            if (s.charAt(i) == c) { return counter; }
            counter++;
        }
        return s.length() + 1;
    }
}
