var shortestToChar = function(s, c) {
    const res = []
    for(let i=0;i<s.length;i++){
        let left = i
        let right = i
        while(left>=0){
            if(s[left] === c) break
            left--
        }
        while(right<s.length){
            if(s[right] === c) break
            right++
        }
        if(left === -1) left = -Infinity
        if(right === s.length) right = Infinity
        res[i] = Math.min(i-left, right-i)
    }
    return res
};
```

