class Solution {

    /**
     * @param String $s
     * @param String $c
     * @return Integer[]
     */
    function shortestToChar($s, $c) {
        $target = [];
        $len = strlen($s);
        $targetLen = 0;
        for ($i=0; $i<$len; $i++) {
            if ($s[$i] == $c) {
                array_push($target, $i);
                $targetLen++;
            }
        }

        for ($i = 0; $i<$len; $i++) {
            if ($s[$i] == $c) {
                $result[] = 0;
            } else {
                $num = $len;
                for ($j = 0; $j<$targetLen;$j++) {
                    $tmp = abs($target[$j] - $i);
                    if ($tmp < $num) {
                        $num = $tmp; 
                    } else {
                        continue;
                    }
                }
                $result[] = $num;
            } 
        }
        return $result;
    }
}

