/**
 * Note: The returned array must be malloced, assume caller calls free().
 */
int* shortestToChar(char * s, char c, int* returnSize){
    size_t string_size = strlen(s);
    *returnSize = string_size;
    if (string_size == 0)
    {
        return NULL;
    }

    int *distance = malloc(string_size * sizeof(int));
    //error handling

    long index_c_first = -1;
    long index_c_second = -1;

    for (long i = 0; i < string_size; ++i)
    {
        if (s[i] == c)
        {
            if (index_c_first == -1)
            {
                index_c_first = i;
                for (long j = index_c_first; j >=0; --j)
                {
                    distance[j] = index_c_first-j;
                }
            }
            else if (index_c_second == -1)
            {
                index_c_second = i;
                for (long j = index_c_second; j > (index_c_second+index_c_first)/2; --j)
                {
                    distance[j] = index_c_second-j;
                }
            }
            else
            {
                long tmp = index_c_second;
                index_c_second = i;
                index_c_first = tmp;
                for (long j = index_c_second; j > (index_c_second+index_c_first)/2; --j)
                {
                    distance[j] = index_c_second-j;
                }
            }
        }
        else
        {
            long index_c = index_c_second == -1 ? index_c_first : index_c_second;
            if (index_c != -1)
            {
                distance[i] = i - index_c;
            }
        }
    }

    return distance;
}
