var shortestToChar = function (s, c) {

    const stackRight = []
    const stackLeft = []
    for (let i = 0; i < s.length; i++) {
        const char = s[i]
        if (char === c) {
            stackRight.unshift(i)
        }
    }
    let i = 0
    const res = []
    while (i < s.length) {
        let topRight = stackRight[stackRight.length - 1]
        if (topRight !== undefined && i > topRight) {
            stackLeft.push(stackRight.pop())
        }
        topRight = stackRight[stackRight.length - 1] === undefined ? Number.MAX_SAFE_INTEGER : stackRight[stackRight.length - 1]
        let topLeft = stackLeft[stackLeft.length - 1] === undefined ? Number.MAX_SAFE_INTEGER : stackLeft[stackLeft.length - 1]
        const minDis = Math.min(Math.abs(i - topLeft), Math.abs(i - topRight))
        i++
        res.push(minDis)
    }
    return res
};
