```C++
class Solution {
public:
    vector<int> shortestToChar(string s, char c) {
        int n = s.length();
        vector<int> res(n);
        
        for (int i = 0; i < n; i++) {
            if (s[i] == c)
                continue;
            int l = i;
            int r = i;
            int len = 0;
            
            while (l >= 0 || r < n) {
                if (s[l] == c) {
                    len = i - l;
                    break;
                }
                if (s[r] == c) {
                    len = r - i;
                    break;
                }

                if (l > 0) l--;
                if (r < n) r++;
            }
            res[i] = len;
        }
        return res;
    }
};
```
