var totalNQueens = function (n) {
  let res = 0;
  const dfs = (n, row, col, pie, na) => {
    if (row >= n) {
      res++;
      return;
    }
    let bits = ~(col | pie | na) & ((1 << n) - 1);
    while (bits) {
      let p = bits & -bits;
      bits = bits & (bits - 1);
      dfs(n, row + 1, col | p, (pie | p) << 1, (na | p) >> 1);
    }
  };
  dfs(n, 0, 0, 0, 0);
  return res;
};
