var maxAreaOfIsland = function (grid) {
  let x = grid[0].length,
    y = grid.length;
  let res = 0;
  function dfs(i, j) {
    if (i >= y || j >= x || j < 0 || i < 0 || grid[i][j] != 1) return 0;
    grid[i][j] = -1;
    return 1 + dfs(i + 1, j) + dfs(i - 1, j) + dfs(i, j - 1) + dfs(i, j + 1);
  }
  for (let i = 0; i < y; i++) {
    for (let j = 0; j < x; j++) {
      if (grid[i][j]) {
        res = Math.max(res, dfs(i, j));
      }
    }
  }
  return res;
};
