class MyQueue:

    def __init__(self):
        self.forward = []
        self.backward = []
        

    def push(self, x: int) -> None:
        self.forward.append(x)
        

    def pop(self) -> int:
        if self.backward:
            return self.backward.pop()
        else:
            while self.forward:
                self.backward.append(self.forward.pop())
            return self.backward.pop()
        

    def peek(self) -> int:
        if self.backward:
            return self.backward[-1]
        return self.forward[0]
        

    def empty(self) -> bool:
        if len(self.forward) or len(self.backward):
            return False
        else:
            return True
