from collections import deque
class Solution:
    def findBottomLeftValue(self, root: Optional[TreeNode]) -> int:
        q = deque([root])
        ans = root.val
        while q:
            next_layer = []
            while q:
                n = q.popleft()
                if n.left:
                    next_layer.append(n.left)
                if n.right:
                    next_layer.append(n.right)
            q = deque(next_layer)
            if next_layer:
                ans = next_layer[0].val
        return ans
