        let newArr = []
        let arr1c = arr1[deep]
        let arr2c = arr2[Math.abs(deep) - 1]
        if (deep >= 0) {
            if (!arr1[deep]) {
                arr1[deep] = [node.val]
                arr1c = arr1[deep]
            } else {
                if (arr1c[arr1c.length - 1] <= node.val) {
