                            newArr.push(node.val)
                        }
                        newArr.push(i)
                    })
                }
                arr1c = newArr
            }
            arr1[deep] = arr1c
        } else {
            if (!arr2[Math.abs(deep) - 1]) {
                arr2[Math.abs(deep) - 1] = [node.val]
                arr2c = arr2[Math.abs(deep) - 1]
            } else {

                if (arr2c[arr2c.length - 1] <= node.val) {
