var minSubarray = function (nums, p) {
    let x = 0
    for (const num of nums) {
        x = (x + num) % p
    }
    if (x === 0) {
        return 0
    }
    const index = new Map()
    let y = 0; res = nums.length
    for (let i = 0; i < nums.length; i++) {
