    return null;
  }

  while (this.queue.size() > 1) {
    this.stack.push(this.queue.pop());
  }

  var head = this.queue.pop();

  while (this.stack.size() > 0) {
    this.queue.push(this.stack.pop());
  }
  return head;
};

/**
