 * @return {ListNode}
 */
var rotateRight = function (head, k) {
  if (!head || (head && !head.next) || k === 0) {
    return head;
  }

  var newHead = null;
  var tailPrev = null;
  var len = 0;

  // 
  while (head) {
    //  1->2->3->4->5->NULL
    const nextNode = head.next;

    // 
    head.next = newHead;
    newHead = head;

    head = nextNode;

    if (newHead && newHead.next && !newHead.next.next) {
      tailPrev = newHead;
    }
    len++;
  }

  for (var i = 0; i < k % len; i++) {
    var headNextNode = newHead.next;
    tailPrev = tailPrev.next;
    tailPrev.next = newHead;
    tailPrev.next.next = null;
    newHead = headNextNode;
  }

  while (newHead) {
    const nextNode = newHead.next;
    // 
    newHead.next = head;
    head = newHead;

    newHead = nextNode;
  }

  return head;
};
