var rotateRight = function (head, k) {
  if (!head || !head.next || k === 0) return head;

  let [n, cur] = [1, head];
  while (cur.next) {
    cur = cur.next;
    n++;
  }

  let gap = n - (k % n);
  if (gap === n) return head;

  cur.next = head;
  while (gap > 0) {
    cur = cur.next;
    gap--;
  }

  const res = cur.next;
  cur.next = null;

  return res;
};
