    let sum = 0;
    let queue = initialBoxes;
    while (queue.length) {
        const i = queue.shift();
        if (status[i]) {
            sum += candies[i];
            queue.push(...containedBoxes[i]);
            for (const key of keys[i]) {
                status[key] = 1;
                if (needKeys.has(key)) {
                    queue.push(key);
                    needKeys.delete(key);
                }
            }
        } else {
            needKeys.add(i)
        }
    }

    return sum;
};
