var rotateRight = function(head, k) {
    if(!head) return head;
    let length = 0;
    let node = head;
    while(node) {
        node = node.next;
        length += 1;
    }
    k = k % length;
    let slow = head, fast = head, result = head;
    while(fast.next) {
        fast = fast.next;
        k -= 1;
        if(k < 0) {
            slow = slow.next; 
        }
    }
    fast.next = head;
    result = slow.next;
    slow.next = null
    return result;
};
