    const serializationRes = []; 
    traverseToSerializeTree(root, serializationRes);
    return serializationRes.join(';'); 


    function traverseToSerializeTree(node, result) {
        if(!node) {
            result.push('NULL');
            return;
        }
        result.push(node.val);
        traverseToSerializeTree(node.left, result);
        traverseToSerializeTree(node.right, result);
    }
};

/**
 * Decodes your encoded data to tree.
 *
 * @param {string} data
 * @return {TreeNode}
 */
var deserialize = function(data) {
    if(!data) return []; 
    const nodeList = data.split(';'); 
    return traverseToGenerateTree(nodeList); 

    function traverseToGenerateTree(nodes) {
        const value = nodes.shift(); 
        if(value === 'NULL'  || !nodes.length) return null;
        const node = new TreeNode(value)
        node.left =  traverseToGenerateTree(nodes);
        node.right = traverseToGenerateTree(nodes);
        return node; 
    }

};

/**
 * @param {TreeNode} root
