var verticalOrder = function(root) {
    if(!root) return []; 
    const nodeList = {}; 
    let min = Infinity
    let max = Infinity * (-1) 
    const queue = [];
    queue.push([root, 0]); 
    while(queue.length) {
        const [current, index] = queue.shift();
        min = Math.min(index, min);
        max = Math.max(index,max);

        if(!nodeList[index]) {
            nodeList[index] = []; 
        }
        nodeList[index].push(current.val)
        if (current.left) {
            queue.push([current.left, index-1]);
        }
        if(current.right) {
           queue.push([current.right, index+1]);
        }
    }

    const result = []; 
    for (let i = min; i < max+ 1; i++) {
        result.push(nodeList[i])
    }
    return result

};

