class Solution(object):
    def maxSlidingWindow(self, nums, k):
        """
        :type nums: List[int]
        :type k: int
        :rtype: List[int]
        """
        queue = MonotonicQueue()
        res = []
        for i in range(len(nums)):
            if i < k - 1:
                queue.push(nums[i])
            else:
                queue.push(nums[i])
                res.append(queue.max())
                queue.pop(nums[i-k+1])
        return res


class MonotonicQueue:
    def __init__(self):
        self.queue = []; 

    def push(self, n):
        while self.queue and self.queue[-1] < n:
            self.queue.pop()
        self.queue.append(n)

    def max(self):
        return self.queue[0]

    def pop(self,n):
        if n == self.queue[0]:
            self.queue.pop(0)

