        n = len(grid); 
        heap = [(grid[0][0], 0,0)]; 
        count = 0
        visited = set(); 
        while heap:
            time, row, col = heapq.heappop(heap); 
            count = max(time, count); 
            if row == n-1 and col == n-1:
                return count; 
            for direction in [(0,-1), (0,1), (1,0), (-1,0)]:
                x = direction[0] + row
                y = direction[1] + col
                if 0 <=x < len(grid) and 0<=y< len(grid[0]) and (x,y) not in visited: 
                    heapq.heappush(heap, (grid[x][y], x,y)); 
                    visited.add((x,y))
        return count; 
        
