class Solution {
public:
    ListNode *detectCycle(ListNode *head) {
        if(head==nullptr || head->next==nullptr || head->next->next==nullptr ) return nullptr;
        ListNode* fast = head;
        ListNode* slow = head;
        do{ 
            if(fast==nullptr) break;
            fast = fast->next;
            if(fast==nullptr) break;           
            fast = fast->next;
            slow = slow->next;
        }while(fast!=slow);  //  while
        if(fast==nullptr||slow==nullptr) return nullptr;
        fast = head;
        while(fast!=slow){
            fast = fast->next;
            slow = slow->next;
        }
        return fast;
    }
};
