class Solution {
public:
    int findBottomLeftValue(TreeNode* root) {
        if(root->left == nullptr && root->right==nullptr) return root->val;
        queue<TreeNode*> q;
        q.push(root);
        int res;
        while(!q.empty()){
            TreeNode* node;// 
            queue<TreeNode*> tmp;
            int n = q.size();
            for(int i=0;i<n; i++){
            node = q.front();
            q.pop();
            if(node->left!=nullptr) tmp.push(node->left);
            if(node->right!=nullptr) tmp.push(node->right);
            }
            q=tmp;
            if(q.empty()) break; // for
            res = tmp.front()->val;
        }
        return res;

    }
};
