                    ans.append(str(combi[0]) + ':0' + str(combi[1]))
                else:
                    ans.append(str(combi[0]) + ':' + str(combi[1]))
            else:
                for i in range(start, h_len + m_len):
                    if i < h_len:
                        combi[0] += hour[i]
                        if combi[0] <= 11:
                            time(combi, led - 1, i + 1)
                        combi[0] -= hour[i]
                    else:
                        combi[1] += minute[i - h_len]
                        if combi[1] <= 59:
                            time(combi, led - 1, i + 1)
                        combi[1] -= minute[i - h_len]       
        cur = 2 * [0]
        time(cur, turnedOn, 0)
        return ans
