class TrieNode:
    def __init__(self):
        self.count = 0 # 
        self.preCount = 0 # 
        self.children = {}
class Trie:

    def __init__(self):
        self.root = TrieNode()

    def insert(self, word: str) -> None:
        node = self.root
        for tmp in word:
            if tmp not in node.children:
                node.children[tmp] = TrieNode()
            node = node.children[tmp]
            node.preCount += 1
        node.count += 1

    def search(self, word: str) -> bool:
        node = self.root
        for tmp in word:
            if tmp not in node.children:
                return False
            node = node.children[tmp]
        return node.count > 0

    def startsWith(self, prefix: str) -> bool:
        node = self.root
        for tmp in prefix:
            if tmp not in node.children:
                return False
            node = node.children[tmp]
        return node.preCount > 0

