            int digit2 = (i < k_num.size()) ? k_num[i] : 0; //  i  k_num  0
            int sum = digit1 + digit2 + carry; // 
            carry = sum / 10; // 
            result.push_back(sum % 10); // 
        }
        // 
        if (carry > 0) 
        {
            result.push_back(carry);
        }
        reverse(result.begin(), result.end());  //
        return result;
    }
