class CustomStack {
private:
    int _size;
    int _maxsize;
    vector<int> stack;

public:
    CustomStack(int maxSize) {
        _maxsize=maxSize;
        _size=0;
    }

    void push(int x) {
        if(_size < _maxsize)
        {
            stack.push_back(x);
            _size++;
        }            
    }

    int pop() {
        if(0 == stack.size())
            return -1;

        int re = stack.back();

        stack.pop_back();
        _size--;                   

        return re;
    }

    void increment(int k, int val) {
        if(k > stack.size())
            k=stack.size();

        int count=0;
        for(auto it = stack.begin(); it != stack.end(); ++it)
        {
            if(count == k)
                break;

            *it += val;
            count++;
        }
    }
};
