    ListNode* rotateRight(ListNode* head, int k) 
    {
        if (head == nullptr || k == 0)
        {
            return head;
        }
        ListNode* tail = head;
        int len = 1;
        while (tail->next)  //
        {
            len++;
            tail = tail->next;
        }
        tail->next = head; //
        k = k % len; // k 
        int step = len - k;//
        ListNode* newtail = head;
        for (int i = 1; i < step; ++i)
        {
            newtail = newtail->next;//
        }
        ListNode* newhead = newtail->next;// 
        newtail->next = nullptr;//
        return newhead;
    }
};
