class Solution {
public:
    ListNode *getIntersectionNode(ListNode *headA, ListNode *headB) {
        vector<ListNode *> headAvec;
        while(headA != nullptr)
        {
            headAvec.push_back(headA);
            headA = headA->next;
        }
        while(headB != nullptr)
        {
            for(auto it = headAvec.begin(); it != headAvec.end(); ++it)
            {
                if(*it == headB)
                {
                    return headB;
                }
            }
            headB = headB->next;
        }
        return headB;
    }
};
