class LRUCache {
private:
    struct ListNode {
        int key;
        int val;
        ListNode* next;
        ListNode* last;
        ListNode() : key(0), val(0), next(nullptr), last(nullptr) {}
        ListNode(int _key, int _value)
            : key(_key), val(_value), next(nullptr), last(nullptr) {}
    };

    ListNode* dummy_head;
    ListNode* dummy_tail;

    unordered_map<int, ListNode*> datamap;
    int max_size;
    int cur_size;

public:
    LRUCache(int capacity) {
        max_size = capacity;

        dummy_head = new ListNode();
        dummy_tail = new ListNode();

        dummy_head->next = dummy_tail;
        dummy_tail->last = dummy_head;
    }

    int get(int key) {
        auto it = datamap.find(key);
        if (it != datamap.end()) {
            //  key 
            // 
            ListNode* cur_node = datamap[key];
            moveToHead(cur_node);
            // 
            return cur_node->val;
        } else {
            return -1;
        }
    }

    void put(int key, int value) {
        auto it = datamap.find(key);
        if (it != datamap.end()) {
            //  key  value
            datamap[key]->val = value;
            // 
            moveToHead(datamap[key]);
        } else //  key-value
        {
            // 
            ListNode* node = new ListNode(key, value);

            // 
            insertToHead(node);
            // 
            datamap[key] = node;

            cur_size++;
            if (cur_size > max_size) {
                //  capacity  
                //  
                ListNode* tail_node = removeTail();
                // 
                datamap.erase(tail_node->key);
                cur_size--;
            }
        }
    }

    void moveToHead(ListNode* node) {
        node->last->next = node->next;
        node->next->last = node->last;
        node->next = dummy_head->next;
        node->next->last = node;
        dummy_head->next = node;
        node->last = dummy_head;
    }

    void insertToHead(ListNode* node) {
        ListNode* temp = dummy_head->next;
        node->next = temp;
        temp->last = node;
        dummy_head->next = node;
        node->last = dummy_head;
    }

    ListNode* removeTail() {
        ListNode* tail_node = dummy_tail->last;
        tail_node->last->next = dummy_tail;
        dummy_tail->last = tail_node->last;
        return tail_node;
    }
};

