    int findBottomLeftValue(TreeNode* root) {
        if (root == nullptr)
            return 0; // 
        queue<TreeNode*> q;
        q.push(root); // 
        while (!q.empty()) {
            TreeNode* current = q.front(); // 
            q.pop(); // 
            // 
            if (current->right != nullptr) {
                q.push(current->right);
            }
            // 
            if (current->left != nullptr) {
                q.push(current->left);
            }
        }
        return q.back()->val;
    }
};
